import { GET_MENU } from "./type";
import axios from "axios";

export const getMenu = (payload) => {
  return {
    type: "GET_MENUClient",
    payload,
  };
};

export const getMenuData = () => {
  return (dispatch) =>
    axios
      .get("http://localhost:8000/menu")
      .then((res) => dispatch(getMenu(res.data)));
};
