import React from "react";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Col, Form,
  FormGroup, Label, Input,} from "reactstrap";

  import { connect } from "react-redux";

class EditCard extends React.Component {
  state = {
    id: this.props.el.id,
    title: this.props.el.title,
    image: this.props.el.image,
    prix: this.props.el.prix,
    description: this.props.el.description,
    toggle: false,
  };

  handleShow = () => {
    this.setState({ toggle: !this.state.toggle });};


  render() {
    return (
      <div>
        <Button color="warning" onClick={this.toggle}>Edit Card</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.props.editMenu}><center>Edit Card</center></ModalHeader>
          <ModalBody>
          <Form className="form">
          <Col>
            <FormGroup>
              <Label>title</Label>
              <Input
              type="text" id="fname" name="fname"  
              defaultValue={this.props.el.title}
              onChange={(e) => this.setState({ title: e.target.value })}
              />
            </FormGroup>
          </Col>

          <Col>
            <FormGroup>
              <Label for="image">image</Label>
              <Input
              type="text"
              placeholder="image..."
              defaultValue={this.props.el.image}
              onChange={(e) => this.setState({ image: e.target.value })}
              />
            </FormGroup>
          </Col> 
          <Col>
            <FormGroup>
          <Label>Description</Label>
              <Input
              type="text"
              placeholder="description..."
              defaultValue={this.props.el.description}
              onChange={(e) => this.setState({ description: e.target.value })}
              />
            </FormGroup>
          </Col>

          <Col>
            <FormGroup>
              <Label for="examplePassword">Price</Label>
              <Input
              type="text"
              placeholder="prix..."
              defaultValue={this.props.el.prix}
              onChange={(e) => this.setState({ prix: e.target.value })}
              />
            </FormGroup>
          </Col> 

          </Form>
          </ModalBody>

          <ModalFooter>
            <Button 
            color="warning"
            onClick={() =>
                this.props.editMenu({
                  id: this.props.el.id,
                  title: this.state.title,
                  image: this.state.image,
                  prix: this.state.prix,
                  description: this.state.title,
                })
              }>
             Edit Card
            </Button>{" "}
            <Button color="secondary" onClick={this.toggle}>
              Cancel
            </Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default EditCard;
