import {GET_Meal} from "./type";
import axios from "axios";




export const getMeal = (payload) => {
  return {
    type: "GET_Meal",
    payload,
  };
};

export const getMenuData = () => {
  return (dispatch) =>
    axios
      .get("http://localhost:8000/menu")
      .then((res) => dispatch(getMeal(res.data)));
};
